import React, { useState, useEffect, useRef, useCallback } from 'react';
import './AppleCatcher.css';

// Предзагрузка изображений
const goodAppleImg = new Image();
goodAppleImg.src = './img/good-apple.png';
const rottenAppleImg = new Image();
rottenAppleImg.src = './img/rotten-apple.png';
const catcherImg = new Image();
catcherImg.src = './img/catcher.png';

const AppleCatcher = ({ onGameOver }) => {
    const [score, setScore] = useState(0);
    const [time, setTime] = useState(60);
    const [apples, setApples] = useState([]);
    const [playerPosition, setPlayerPosition] = useState(50);
    const [gameStarted, setGameStarted] = useState(false);
    const [showResult, setShowResult] = useState(false);

    const canvasRef = useRef(null);
    const backgroundMusicRef = useRef(null);
    const buttonClickSoundRef = useRef(null);
    const lastUpdateTimeRef = useRef(0);
    const animationFrameRef = useRef(null);

    const checkCollisions = useCallback(() => {
        setApples((prevApples) =>
            prevApples.filter((apple) => {
                if (
                    apple.y > 70 &&
                    apple.y < 85 &&
                    Math.abs(apple.x - playerPosition) < 15
                ) {
                    const scoreChange = apple.type === 'good' ? 10 : -20;
                    setScore((prevScore) => prevScore + scoreChange);
                    return false;
                }
                return apple.y < 100;
            })
        );
    }, [playerPosition]);

    const gameLoop = useCallback((timestamp) => {
        if (!lastUpdateTimeRef.current) {
            lastUpdateTimeRef.current = timestamp;
        }
        const deltaTime = timestamp - lastUpdateTimeRef.current;
        
        if (deltaTime >= 33) { // Примерно 30 FPS для мобильных устройств
            lastUpdateTimeRef.current = timestamp;
            
            setApples((prevApples) => {
                const newApples = prevApples.map((apple) => ({
                    ...apple,
                    y: apple.y + 0.5 * (deltaTime / 33),
                }));
        
                if (Math.random() < 0.02 && newApples.length < 4) {
                    newApples.push({
                        id: Date.now(),
                        x: Math.random() * 100,
                        y: 0,
                        type: Math.random() > 0.2 ? 'good' : 'rotten',
                    });
                }
        
                return newApples.filter((apple) => apple.y < 100);
            });
        
            checkCollisions();
            drawGame();
        }
    
        animationFrameRef.current = requestAnimationFrame(gameLoop);
    }, [checkCollisions]);

    const drawGame = useCallback(() => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        const ctx = canvas.getContext('2d');
        ctx.clearRect(0, 0, canvas.width, canvas.height);

        // Отрисовка яблок
        apples.forEach((apple) => {
            const img = apple.type === 'good' ? goodAppleImg : rottenAppleImg;
            ctx.drawImage(img, apple.x * canvas.width / 100, apple.y * canvas.height / 100, 30, 30);
        });

        // Отрисовка игрока
        ctx.drawImage(catcherImg, playerPosition * canvas.width / 100 - 45, canvas.height - 90, 90, 90);

        // Отрисовка счета и времени
        ctx.font = '20px Arial';
        ctx.fillStyle = 'white';
        ctx.fillText(`Score: ${score}`, 10, 30);
        ctx.fillText(`Time: ${time}`, canvas.width - 100, 30);
    }, [apples, playerPosition, score, time]);

    useEffect(() => {
        const canvas = canvasRef.current;
        if (canvas) {
            canvas.width = window.innerWidth;
            canvas.height = window.innerHeight;
        }

        backgroundMusicRef.current = new Audio('./audio/background-music.mp3');
        backgroundMusicRef.current.loop = true;
        buttonClickSoundRef.current = new Audio('./audio/button-click.mp3');

        return () => {
            if (backgroundMusicRef.current) {
                backgroundMusicRef.current.pause();
                backgroundMusicRef.current = null;
            }
            if (buttonClickSoundRef.current) {
                buttonClickSoundRef.current = null;
            }
        };
    }, []);

    useEffect(() => {
        if (gameStarted && time > 0) {
            const timer = setInterval(() => {
                setTime((prevTime) => prevTime - 1);
            }, 1000);

            return () => clearInterval(timer);
        } else if (time === 0) {
            endGame();
        }
    }, [gameStarted, time]);

    useEffect(() => {
        if (gameStarted) {
            if (backgroundMusicRef.current) {
                backgroundMusicRef.current.play().catch(error => console.error("Error playing audio:", error));
            }
            animationFrameRef.current = requestAnimationFrame(gameLoop);
        } else {
            if (animationFrameRef.current) {
                cancelAnimationFrame(animationFrameRef.current);
            }
            if (backgroundMusicRef.current) {
                backgroundMusicRef.current.pause();
            }
        }

        return () => {
            if (animationFrameRef.current) {
                cancelAnimationFrame(animationFrameRef.current);
            }
        };
    }, [gameStarted, gameLoop]);

    const playButtonClickSound = () => {
        if (buttonClickSoundRef.current) {
            buttonClickSoundRef.current.currentTime = 0;
            buttonClickSoundRef.current.play().catch(error => console.error("Error playing audio:", error));
        }
    };

    const startGame = () => {
        playButtonClickSound();
        setGameStarted(true);
        setScore(0);
        setTime(60);
        setApples([]);
        setShowResult(false);
        lastUpdateTimeRef.current = 0;
    };

    const endGame = () => {
        setGameStarted(false);
        setShowResult(true);
        if (backgroundMusicRef.current) {
            backgroundMusicRef.current.pause();
        }
    };

    const movePlayer = useCallback((e) => {
        if (gameStarted) {
            const touch = e.touches[0];
            const newPosition = (touch.clientX / window.innerWidth) * 100;
            setPlayerPosition(newPosition);
        }
    }, [gameStarted]);

    const handleResultClose = () => {
        playButtonClickSound();
        setShowResult(false);
        onGameOver(score);
    };

    return (
        <div className="apple-catcher" onTouchMove={movePlayer}>
            <canvas ref={canvasRef} />
            {!gameStarted && !showResult && (
                <button className="start-button" onClick={startGame}>Start Game</button>
            )}
            {showResult && (
                <div className="result-modal">
                    <div className="result-content">
                        <h2>Game end!</h2>
                        <p>Your score: {score}</p>
                        <button onClick={handleResultClose}>OK</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AppleCatcher;
