import React, { useReducer, useEffect, useRef, useCallback } from 'react';
import './AppleCatcher.css';
import throttle from 'lodash/throttle';

import goodAppleImg from './img/good-apple.png';
import rottenAppleImg from './img/rotten-apple.png';
import catcherImg from './img/catcher.png';
import backgroundMusic from './audio/background-music.mp3';
import buttonClickSound from './audio/button-click.mp3';

const CANVAS_WIDTH = 800;
const CANVAS_HEIGHT = 600;

const initialState = {
  score: 0,
  time: 60,
  apples: [],
  playerPosition: 50,
  gameStarted: false,
  showResult: false,
  scoreAnimations: []
};

function gameReducer(state, action) {
  switch (action.type) {
    case 'START_GAME':
      return { ...initialState, gameStarted: true };
    case 'END_GAME':
      return { ...state, gameStarted: false, showResult: true };
    case 'UPDATE_PLAYER_POSITION':
      return { ...state, playerPosition: action.payload };
    case 'UPDATE_GAME_STATE':
      return { ...state, ...action.payload };
    case 'ADD_SCORE_ANIMATION':
      return { 
        ...state, 
        scoreAnimations: [...state.scoreAnimations, action.payload],
        score: state.score + action.payload.score
      };
    case 'REMOVE_SCORE_ANIMATION':
      return {
        ...state,
        scoreAnimations: state.scoreAnimations.filter(anim => anim.id !== action.payload)
      };
    default:
      return state;
  }
}

const AppleCatcher = ({ onGameOver }) => {
  const [state, dispatch] = useReducer(gameReducer, initialState);
  const canvasRef = useRef(null);
  const backgroundMusicRef = useRef(new Audio(backgroundMusic));
  const buttonClickSoundRef = useRef(new Audio(buttonClickSound));
  const gameLoopRef = useRef(null);
  const lastUpdateTimeRef = useRef(0);

  const imagesRef = useRef({
    goodApple: new Image(),
    rottenApple: new Image(),
    catcher: new Image()
  });

  const isTouchDevice = useRef('ontouchstart' in window || navigator.maxTouchPoints > 0);

  useEffect(() => {
    imagesRef.current.goodApple.src = goodAppleImg;
    imagesRef.current.rottenApple.src = rottenAppleImg;
    imagesRef.current.catcher.src = catcherImg;
    
    const bgMusic = backgroundMusicRef.current;
    bgMusic.loop = true;

    return () => {
      bgMusic.pause();
    };
  }, []);

  const checkCollisions = useCallback(() => {
    const catcherWidth = CANVAS_WIDTH * 0.1;
    const catcherHeight = CANVAS_WIDTH * 0.1;
    const catcherTop = CANVAS_HEIGHT * 0.8;
    const catcherBottom = catcherTop + catcherHeight;
    const catcherCenterX = state.playerPosition * CANVAS_WIDTH / 100;
    const catcherLeft = catcherCenterX - catcherWidth / 2;
    const catcherRight = catcherCenterX + catcherWidth / 2;

    const newApples = state.apples.filter(apple => {
      const appleSize = CANVAS_WIDTH * 0.05;
      const appleLeft = apple.x;
      const appleRight = apple.x + appleSize;
      const appleBottom = apple.y + appleSize;
      
      if (
        appleBottom >= catcherTop &&
        appleBottom <= catcherBottom &&
        appleRight >= catcherLeft &&
        appleLeft <= catcherRight
      ) {
        const scoreChange = apple.type === 'good' ? 10 : -20;
        dispatch({
          type: 'ADD_SCORE_ANIMATION',
          payload: {
            id: Date.now(),
            x: catcherCenterX,
            y: catcherTop,
            score: scoreChange
          }
        });
        setTimeout(() => {
          dispatch({ type: 'REMOVE_SCORE_ANIMATION', payload: Date.now() });
        }, 1000);
        return false;
      }
      return apple.y < CANVAS_HEIGHT;
    });

    dispatch({ type: 'UPDATE_GAME_STATE', payload: { apples: newApples } });
  }, [state.apples, state.playerPosition]);

  const renderGame = useCallback(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);

    // Рисуем фон (просто заливка цветом, так как у нас нет изображения фона)
    ctx.fillStyle = '#87CEEB'; // Цвет неба
    ctx.fillRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);

    // Рисуем яблоки
    state.apples.forEach(apple => {
      const img = apple.type === 'good' ? imagesRef.current.goodApple : imagesRef.current.rottenApple;
      ctx.drawImage(img, apple.x, apple.y, CANVAS_WIDTH * 0.05, CANVAS_WIDTH * 0.05);
    });

    // Рисуем игрока
    const catcherWidth = CANVAS_WIDTH * 0.1;
    const catcherHeight = CANVAS_WIDTH * 0.1;
    const playerX = state.playerPosition * CANVAS_WIDTH / 100 - catcherWidth / 2;
    const playerY = CANVAS_HEIGHT * 0.8;
    ctx.drawImage(imagesRef.current.catcher, playerX, playerY, catcherWidth, catcherHeight);

    // Рисуем анимации счета
    ctx.font = '20px Arial';
    state.scoreAnimations.forEach(anim => {
      ctx.fillStyle = anim.score > 0 ? 'green' : 'red';
      ctx.fillText(`${anim.score > 0 ? '+' : ''}${anim.score}`, anim.x, anim.y);
    });

    // Рисуем счет и время
    ctx.fillStyle = 'black';
    ctx.font = '24px Arial';
    ctx.fillText(`Score: ${state.score}`, 10, 30);
    ctx.fillText(`Time: ${state.time}`, CANVAS_WIDTH - 100, 30);
  }, [state]);

  const gameLoop = useCallback((timestamp) => {
    if (timestamp - lastUpdateTimeRef.current > 16) { // Примерно 60 FPS
      lastUpdateTimeRef.current = timestamp;
      
      const newApples = state.apples
        .map(apple => ({ ...apple, y: apple.y + CANVAS_HEIGHT * 0.005 }))
        .filter(apple => apple.y < CANVAS_HEIGHT);

      if (Math.random() < 0.02 && newApples.length < 5) {
        newApples.push({
          id: Date.now(),
          x: Math.random() * (CANVAS_WIDTH - CANVAS_WIDTH * 0.05),
          y: 0,
          type: Math.random() > 0.2 ? 'good' : 'rotten',
        });
      }

      dispatch({ type: 'UPDATE_GAME_STATE', payload: { apples: newApples } });
      checkCollisions();
      renderGame();
    }

    gameLoopRef.current = requestAnimationFrame(gameLoop);
  }, [state.apples, checkCollisions, renderGame]);

  const endGame = useCallback(() => {
    dispatch({ type: 'END_GAME' });
    backgroundMusicRef.current.pause();
  }, []);

  useEffect(() => {
    if (state.gameStarted && state.time > 0) {
      const timer = setInterval(() => {
        dispatch({ type: 'UPDATE_GAME_STATE', payload: { time: state.time - 1 } });
      }, 1000);

      return () => clearInterval(timer);
    } else if (state.time === 0) {
      endGame();
    }
  }, [state.gameStarted, state.time, endGame]);

  useEffect(() => {
    if (state.gameStarted) {
      backgroundMusicRef.current.play();
      gameLoopRef.current = requestAnimationFrame(gameLoop);
    } else {
      cancelAnimationFrame(gameLoopRef.current);
      backgroundMusicRef.current.pause();
    }

    return () => cancelAnimationFrame(gameLoopRef.current);
  }, [state.gameStarted, gameLoop]);

  const playButtonClickSound = useCallback(() => {
    buttonClickSoundRef.current.currentTime = 0;
    buttonClickSoundRef.current.play();
  }, []);

  const startGame = useCallback(() => {
    playButtonClickSound();
    dispatch({ type: 'START_GAME' });
  }, [playButtonClickSound]);

  const movePlayer = useCallback(throttle((e) => {
    if (state.gameStarted) {
      const canvasRect = canvasRef.current.getBoundingClientRect();
      const catcherWidth = CANVAS_WIDTH * 0.1;
      let newPosition;

      if (isTouchDevice.current) {
        newPosition = ((e.touches[0].clientX - canvasRect.left) / canvasRect.width) * 100;
      } else {
        newPosition = ((e.clientX - canvasRect.left) / canvasRect.width) * 100;
      }
      
      // Ограничение движения ловца границами экрана
      newPosition = Math.max(catcherWidth / 2 / CANVAS_WIDTH * 100, newPosition);
      newPosition = Math.min(100 - catcherWidth / 2 / CANVAS_WIDTH * 100, newPosition);
      
      dispatch({ type: 'UPDATE_PLAYER_POSITION', payload: newPosition });
    }
  }, 16), [state.gameStarted]);

  const handleResultClose = useCallback(() => {
    playButtonClickSound();
    dispatch({ type: 'UPDATE_GAME_STATE', payload: { showResult: false } });
    onGameOver(Math.max(0, state.score));
  }, [playButtonClickSound, state.score, onGameOver]);

  return (
    <div className="apple-catcher">
      <canvas 
        ref={canvasRef} 
        width={CANVAS_WIDTH} 
        height={CANVAS_HEIGHT} 
        onMouseMove={isTouchDevice.current ? undefined : movePlayer}
        onTouchMove={isTouchDevice.current ? movePlayer : undefined}
      />
      {!state.gameStarted && !state.showResult && (
        <button className="start-button" onClick={startGame}>Start Game</button>
      )}
      {state.showResult && (
        <div className="result-modal">
          <div className="result-content">
            <h2>Game Over!</h2>
            <p>Your score: {Math.max(0, state.score)}</p>
            <button onClick={handleResultClose}>OK</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default React.memo(AppleCatcher);
