import React, { useState, useEffect, useRef, useCallback } from 'react';
import './AppleCatcher.css';
import goodAppleImg from './img/good-apple.png';
import rottenAppleImg from './img/rotten-apple.png';
import catcherImg from './img/catcher.png';
import backgroundMusic from './audio/background-music.mp3';
import buttonClickSound from './audio/button-click.mp3';

const AppleCatcher = ({ onGameOver }) => {
    const [score, setScore] = useState(0);
    const [time, setTime] = useState(60);
    const [gameStarted, setGameStarted] = useState(false);
    const [showResult, setShowResult] = useState(false);
    const [scoreAnimations, setScoreAnimations] = useState([]);

    const canvasRef = useRef(null);
    const applesRef = useRef([]);
    const playerPositionRef = useRef(50);
    const backgroundMusicRef = useRef(new Audio(backgroundMusic));
    const buttonClickSoundRef = useRef(new Audio(buttonClickSound));
    const animationFrameRef = useRef(null);
    const lastUpdateTimeRef = useRef(0);
    const gameActiveRef = useRef(false);

    const images = useRef({
        goodApple: new Image(),
        rottenApple: new Image(),
        catcher: new Image(),
    });

    useEffect(() => {
        images.current.goodApple.src = goodAppleImg;
        images.current.rottenApple.src = rottenAppleImg;
        images.current.catcher.src = catcherImg;

        const canvas = canvasRef.current;
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight;

        backgroundMusicRef.current.loop = true;

        return () => {
            backgroundMusicRef.current.pause();
            if (animationFrameRef.current) {
                cancelAnimationFrame(animationFrameRef.current);
            }
        };
    }, []);

    const checkCollisions = useCallback(() => {
        if (!gameActiveRef.current) return;

        const canvas = canvasRef.current;
        const playerWidth = canvas.width * 0.2;
        const appleWidth = canvas.width * 0.05;
        const collisionThreshold = (playerWidth + appleWidth) / 2;

        applesRef.current = applesRef.current.filter((apple) => {
            const playerCenterX = playerPositionRef.current * canvas.width / 100;
            const appleCenterX = apple.x * canvas.width / 100;

            if (
                apple.y > canvas.height * 0.75 &&
                apple.y < canvas.height * 0.9 &&
                Math.abs(appleCenterX - playerCenterX) < collisionThreshold
            ) {
                const scoreChange = apple.type === 'good' ? 10 : -10;
                setScore(prevScore => prevScore + scoreChange);
                
                // Добавляем анимацию очков
                setScoreAnimations(prev => [...prev, {
                    id: Date.now(),
                    x: appleCenterX,
                    y: canvas.height * 0.85,
                    score: scoreChange,
                    opacity: 1
                }]);

                return false;
            }
            return apple.y < canvas.height;
        });
    }, []);

    const updateGameState = useCallback((deltaTime) => {
        if (!gameActiveRef.current) return;

        const fallSpeed = 0.07; // Увеличено на 40%
        applesRef.current = applesRef.current.map(apple => ({
            ...apple,
            y: apple.y + fallSpeed * deltaTime,
        }));

        if (Math.random() < 0.01 && applesRef.current.length < 5) {
            applesRef.current.push({
                id: Date.now(),
                x: Math.random() * 100,
                y: 0,
                type: Math.random() > 0.74 ? 'good' : 'rotten', // Увеличена вероятность плохих яблок на 30%
            });
        }

        // Обновляем анимации очков
        setScoreAnimations(prev => 
            prev.map(anim => ({
                ...anim,
                y: anim.y - 0.5,
                opacity: anim.opacity - 0.02
            })).filter(anim => anim.opacity > 0)
        );

        checkCollisions();
    }, [checkCollisions]);

    const drawGame = useCallback(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        // Рисуем голубой фон
        ctx.fillStyle = 'skyblue';
        ctx.fillRect(0, 0, canvas.width, canvas.height);

        // Рисуем яблоки
        applesRef.current.forEach(apple => {
            const img = apple.type === 'good' ? images.current.goodApple : images.current.rottenApple;
            const appleSize = canvas.width * 0.05;
            ctx.drawImage(img, apple.x * canvas.width / 100 - appleSize / 2, apple.y, appleSize, appleSize);
        });

        // Рисуем игрока
        const playerWidth = canvas.width * 0.2;
        const playerHeight = playerWidth * (90 / 90);
        const playerY = canvas.height * 0.85;
        ctx.drawImage(images.current.catcher, playerPositionRef.current * canvas.width / 100 - playerWidth / 2, playerY - playerHeight, playerWidth, playerHeight);

        // Рисуем анимации очков
        scoreAnimations.forEach(anim => {
            ctx.font = '24px Arial';
            ctx.fillStyle = `rgba(${anim.score > 0 ? '0, 255, 0' : '255, 0, 0'}, ${anim.opacity})`;
            ctx.fillText(`${anim.score > 0 ? '+' : ''}${anim.score}`, anim.x, anim.y);
        });

        // Рисуем счет и время
        ctx.font = '20px Arial';
        ctx.fillStyle = 'black';
        ctx.fillText(`Score: ${score}`, 10, 30);
        ctx.fillText(`Time: ${time}`, canvas.width - 100, 30);
    }, [score, time, scoreAnimations]);

    const gameLoop = useCallback((timestamp) => {
        if (!gameActiveRef.current) return;

        if (!lastUpdateTimeRef.current) {
            lastUpdateTimeRef.current = timestamp;
        }
        const deltaTime = timestamp - lastUpdateTimeRef.current;
        lastUpdateTimeRef.current = timestamp;

        updateGameState(deltaTime);
        drawGame();

        animationFrameRef.current = requestAnimationFrame(gameLoop);
    }, [updateGameState, drawGame]);

    useEffect(() => {
        if (gameStarted && time > 0) {
            gameActiveRef.current = true;
            const timer = setInterval(() => {
                setTime(prevTime => {
                    if (prevTime <= 1) {
                        clearInterval(timer);
                        endGame();
                        return 0;
                    }
                    return prevTime - 1;
                });
            }, 1000);

            return () => clearInterval(timer);
        }
    }, [gameStarted]);

    useEffect(() => {
        if (gameStarted && gameActiveRef.current) {
            backgroundMusicRef.current.play().catch(console.error);
            animationFrameRef.current = requestAnimationFrame(gameLoop);
        } else {
            backgroundMusicRef.current.pause();
            if (animationFrameRef.current) {
                cancelAnimationFrame(animationFrameRef.current);
            }
        }
    }, [gameStarted, gameLoop]);

    const startGame = () => {
        buttonClickSoundRef.current.play().catch(console.error);
        setGameStarted(true);
        setScore(0);
        setTime(60);
        applesRef.current = [];
        setShowResult(false);
        gameActiveRef.current = true;
    };

    const endGame = () => {
        setGameStarted(false);
        gameActiveRef.current = false;
        setShowResult(true);
        backgroundMusicRef.current.pause();
        if (animationFrameRef.current) {
            cancelAnimationFrame(animationFrameRef.current);
        }
    };

    const movePlayer = useCallback((e) => {
        if (gameActiveRef.current) {
            const touch = e.touches[0];
            playerPositionRef.current = (touch.clientX / window.innerWidth) * 100;
        }
    }, []);

    const handleResultClose = () => {
        buttonClickSoundRef.current.play().catch(console.error);
        setShowResult(false);
        onGameOver(score);
    };

    return (
        <div className="apple-catcher" onTouchMove={movePlayer}>
            <canvas ref={canvasRef} className="game-canvas" />
            {!gameStarted && !showResult && (
                <button className="start-button" onClick={startGame}>Start Game</button>
            )}
            {showResult && (
                <div className="result-modal">
                    <div className="result-content">
                        <h2>Game Over!</h2>
                        <p>Your score: {score}</p>
                        <button onClick={handleResultClose}>OK</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AppleCatcher;
