import React, { useState, useEffect, useRef, useCallback } from 'react';
import './AppleCatcher.css';
import goodAppleImg from './img/good-apple.png';
import rottenAppleImg from './img/rotten-apple.png';
import catcherImg from './img/catcher.png';
import backgroundMusic from './audio/background-music.mp3';
import buttonClickSound from './audio/button-click.mp3';

const AppleCatcher = ({ onGameOver }) => {
    const [score, setScore] = useState(0);
    const [time, setTime] = useState(60);
    const [gameStarted, setGameStarted] = useState(false);
    const [showResult, setShowResult] = useState(false);

    const applesRef = useRef([]);
    const playerPositionRef = useRef(50);
    const scoreAnimationsRef = useRef([]);
    const backgroundMusicRef = useRef(new Audio(backgroundMusic));
    const buttonClickSoundRef = useRef(new Audio(buttonClickSound));
    const gameLoopRef = useRef(null);
    const canvasRef = useRef(null);

    const checkCollisions = useCallback(() => {
        applesRef.current = applesRef.current.filter((apple) => {
            if (
                apple.y > 70 &&
                apple.y < 85 &&
                Math.abs(apple.x - playerPositionRef.current) < 15
            ) {
                const scoreChange = apple.type === 'good' ? 10 : -20;
                setScore((prevScore) => prevScore + scoreChange);
                
                const animationId = Date.now();
                scoreAnimationsRef.current.push({
                    id: animationId,
                    x: apple.x,
                    y: apple.y,
                    score: scoreChange
                });
                
                setTimeout(() => {
                    scoreAnimationsRef.current = scoreAnimationsRef.current.filter(anim => anim.id !== animationId);
                }, 1000);

                return false;
            }
            return apple.y < 100;
        });
    }, []);

    const gameLoop = useCallback(() => {
        if (!gameStarted) return;

        applesRef.current = applesRef.current.map((apple) => ({
            ...apple,
            y: apple.y + 0.5,
        }));

        if (Math.random() < 0.01 && applesRef.current.length < 5) {
            applesRef.current.push({
                id: Date.now(),
                x: Math.random() * 100,
                y: 0,
                type: Math.random() > 0.2 ? 'good' : 'rotten',
            });
        }

        applesRef.current = applesRef.current.filter((apple) => apple.y < 100);

        checkCollisions();
        drawGame();

        gameLoopRef.current = requestAnimationFrame(gameLoop);
    }, [gameStarted, checkCollisions]);

    const drawGame = useCallback(() => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        const ctx = canvas.getContext('2d');
        ctx.clearRect(0, 0, canvas.width, canvas.height);

        // Отрисовка яблок
        applesRef.current.forEach((apple) => {
            const img = new Image();
            img.src = apple.type === 'good' ? goodAppleImg : rottenAppleImg;
            ctx.drawImage(img, apple.x * canvas.width / 100, apple.y * canvas.height / 100, 30, 30);
        });

        // Отрисовка игрока
        const playerImg = new Image();
        playerImg.src = catcherImg;
        ctx.drawImage(playerImg, playerPositionRef.current * canvas.width / 100 - 45, canvas.height - 90, 90, 90);

        // Отрисовка счета и времени
        ctx.font = '20px Arial';
        ctx.fillStyle = 'white';
        ctx.fillText(`Score: ${score}`, 10, 30);
        ctx.fillText(`Time: ${time}`, canvas.width - 100, 30);

        // Отрисовка анимаций счета
        scoreAnimationsRef.current.forEach((anim) => {
            ctx.fillStyle = anim.score > 0 ? 'green' : 'red';
            ctx.fillText(anim.score > 0 ? `+${anim.score}` : anim.score, anim.x * canvas.width / 100, anim.y * canvas.height / 100);
        });
    }, [score, time]);

    useEffect(() => {
        backgroundMusicRef.current.loop = true;
        const canvas = canvasRef.current;
        if (canvas) {
            canvas.width = window.innerWidth;
            canvas.height = window.innerHeight;
        }
        return () => {
            backgroundMusicRef.current.pause();
        };
    }, []);

    useEffect(() => {
        if (gameStarted && time > 0) {
            const timer = setInterval(() => {
                setTime((prevTime) => prevTime - 1);
            }, 1000);

            return () => clearInterval(timer);
        } else if (time === 0) {
            endGame();
        }
    }, [gameStarted, time]);

    useEffect(() => {
        if (gameStarted) {
            backgroundMusicRef.current.play().catch(error => console.error("Error playing audio:", error));
            gameLoopRef.current = requestAnimationFrame(gameLoop);
        } else {
            if (gameLoopRef.current) {
                cancelAnimationFrame(gameLoopRef.current);
            }
            backgroundMusicRef.current.pause();
        }

        return () => {
            if (gameLoopRef.current) {
                cancelAnimationFrame(gameLoopRef.current);
            }
        };
    }, [gameStarted, gameLoop]);

    const playButtonClickSound = () => {
        buttonClickSoundRef.current.currentTime = 0;
        buttonClickSoundRef.current.play().catch(error => console.error("Error playing audio:", error));
    };

    const startGame = () => {
        playButtonClickSound();
        setGameStarted(true);
        setScore(0);
        setTime(60);
        applesRef.current = [];
        scoreAnimationsRef.current = [];
        setShowResult(false);
    };

    const endGame = () => {
        setGameStarted(false);
        setShowResult(true);
        backgroundMusicRef.current.pause();
    };

    const movePlayer = useCallback((e) => {
        if (gameStarted) {
            const touch = e.touches[0];
            playerPositionRef.current = (touch.clientX / window.innerWidth) * 100;
        }
    }, [gameStarted]);

    const handleResultClose = () => {
        playButtonClickSound();
        setShowResult(false);
        onGameOver(score);
    };

    return (
        <div className="apple-catcher" onTouchMove={movePlayer}>
            <canvas ref={canvasRef} className="game-canvas" />
            {!gameStarted && !showResult && (
                <button className="start-button" onClick={startGame}>Start Game</button>
            )}
            {showResult && (
                <div className="result-modal">
                    <div className="result-content">
                        <h2>Game end!</h2>
                        <p>Your score: {score}</p>
                        <button onClick={handleResultClose}>OK</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AppleCatcher;
