import React, { useState, useEffect, useRef, useCallback } from 'react';
import './AppleCatcher.css';
import goodAppleImg from './img/good-apple.png';
import rottenAppleImg from './img/rotten-apple.png';
import catcherImg from './img/catcher.png';
import backgroundMusic from './audio/background-music.mp3';
import buttonClickSound from './audio/button-click.mp3';

const AppleCatcher = ({ onGameOver }) => {
    const [score, setScore] = useState(0);
    const [time, setTime] = useState(60);
    const [apples, setApples] = useState([]);
    const [playerPosition, setPlayerPosition] = useState(50);
    const [gameStarted, setGameStarted] = useState(false);
    const [scoreAnimations, setScoreAnimations] = useState([]);
    const [showResult, setShowResult] = useState(false);

    const backgroundMusicRef = useRef(new Audio(backgroundMusic));
    const buttonClickSoundRef = useRef(new Audio(buttonClickSound));
    const gameLoopRef = useRef(null);
    const gameAreaRef = useRef(null);

    const checkCollisions = useCallback(() => {
        setApples((prevApples) =>
            prevApples.filter((apple) => {
                if (
                    apple.y > 80 &&
                    apple.y < 95 &&
                    Math.abs(apple.x - playerPosition) < 10
                ) {
                    const scoreChange = apple.type === 'good' ? 10 : -10;
                    setScore((prevScore) => prevScore + scoreChange);
                    
                    const animationId = Date.now();
                    setScoreAnimations((prev) => [...prev, {
                        id: animationId,
                        x: apple.x,
                        y: 85, // Показываем анимацию чуть выше ловца
                        score: scoreChange
                    }]);
                    
                    // Удаляем анимацию через 1 секунду
                    setTimeout(() => {
                        setScoreAnimations((prev) => prev.filter(anim => anim.id !== animationId));
                    }, 1000);

                    return false;
                }
                return apple.y < 100;
            })
        );
    }, [playerPosition]);

    const gameLoop = useCallback(() => {
        setApples((prevApples) => {
            const newApples = prevApples.map((apple) => ({
                ...apple,
                y: apple.y + 0.55,
            }));
    
            if (Math.random() < 0.02 && newApples.length < 5) {
                newApples.push({
                    id: Date.now(),
                    x: Math.random() * 100,
                    y: 0,
                    type: Math.random() > 0.2 ? 'good' : 'rotten',
                });
            }
    
            return newApples.filter((apple) => apple.y < 100);
        });
    
        checkCollisions();
    
        gameLoopRef.current = requestAnimationFrame(gameLoop);
    }, [checkCollisions]);

    useEffect(() => {
        backgroundMusicRef.current.loop = true;
        return () => {
            backgroundMusicRef.current.pause();
        };
    }, []);

    useEffect(() => {
        if (gameStarted && time > 0) {
            const timer = setInterval(() => {
                setTime((prevTime) => prevTime - 1);
            }, 1000);

            return () => clearInterval(timer);
        } else if (time === 0) {
            endGame();
        }
    }, [gameStarted, time]);

    useEffect(() => {
        if (gameStarted) {
            backgroundMusicRef.current.play().catch(console.error);
            gameLoopRef.current = requestAnimationFrame(gameLoop);
        } else {
            if (gameLoopRef.current) {
                cancelAnimationFrame(gameLoopRef.current);
            }
            backgroundMusicRef.current.pause();
        }

        return () => {
            if (gameLoopRef.current) {
                cancelAnimationFrame(gameLoopRef.current);
            }
        };
    }, [gameStarted, gameLoop]);

    const startGame = () => {
        buttonClickSoundRef.current.play().catch(console.error);
        setGameStarted(true);
        setScore(0);
        setTime(60);
        setApples([]);
        setShowResult(false);
    };

    const endGame = () => {
        setGameStarted(false);
        setShowResult(true);
        backgroundMusicRef.current.pause();
    };

    const movePlayer = (e) => {
        if (gameStarted) {
            const touch = e.touches[0];
            const gameArea = gameAreaRef.current.getBoundingClientRect();
            const newPosition = ((touch.clientX - gameArea.left) / gameArea.width) * 100;
            setPlayerPosition(Math.max(0, Math.min(100, newPosition)));
        }
    };

    const handleResultClose = () => {
        buttonClickSoundRef.current.play().catch(console.error);
        setShowResult(false);
        onGameOver(score);
    };

    return (
        <div className="apple-catcher" ref={gameAreaRef} onTouchMove={movePlayer}>
            <div className="game-info">
                <span>Score: {score}</span>
                <span>Time: {time}</span>
            </div>
            {!gameStarted && !showResult && (
                <button className="start-button" onClick={startGame}>Start Game</button>
            )}
            <div className="game-area">
                {apples.map((apple) => (
                    <img
                        key={apple.id}
                        src={apple.type === 'good' ? goodAppleImg : rottenAppleImg}
                        alt={apple.type === 'good' ? 'Good Apple' : 'Rotten Apple'}
                        className="apple"
                        style={{ 
                            left: `${apple.x}%`, 
                            top: `${apple.y}%`,
                        }}
                    />
                ))}
                {scoreAnimations.map((anim) => (
                    <div
                        key={anim.id}
                        className={`score-animation ${anim.score > 0 ? 'positive' : 'negative'}`}
                        style={{ 
                            left: `${anim.x}%`, 
                            top: `${anim.y}%`,
                        }}
                    >
                        {anim.score > 0 ? `+${Math.abs(anim.score)}` : `-${Math.abs(anim.score)}`}
                    </div>
                ))}
                <img
                    src={catcherImg}
                    alt="Catcher"
                    className="player"
                    style={{ 
                        left: `${playerPosition}%`,
                    }}
                />
            </div>
            {showResult && (
                <div className="result-modal">
                    <div className="result-content">
                        <h2>Game end!</h2>
                        <p>Your score: {score}</p>
                        <button onClick={handleResultClose}>OK</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AppleCatcher;
